import React, { useState } from 'react';
import './CropForm.css'; // Importing the CSS

function CropForm() {
    const [formData, setFormData] = useState({
        N: '',
        P: '',
        K: '',
        temperature: '',
        humidity: '',
        ph: '',
        rainfall: ''
    });

    const placeholders = {
        N: 'Nitrogen value (e.g., 90)',
        P: 'Phosphorus value (e.g., 42)',
        K: 'Potassium value (e.g., 43)',
        temperature: 'Temperature in Â°C',
        humidity: 'Humidity (%)',
        ph: 'pH value (e.g., 6.5)',
        rainfall: 'Rainfall (mm)'
    };

    const [result, setResult] = useState('');

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setResult('');
        try {
            const response = await fetch('http://localhost:8000/api/recommend-crop/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(formData)
            });

            const data = await response.json();
            if (response.ok) {
                setResult(data.recommended_crop);
            } else {
                setResult(`Error: ${data.error}`);
            }
        } catch (err) {
            setResult('Server error. Try again later.');
        }
    };

    return (
        <div className="crop-form-container">
            <div className="form-card">
                <h2>ðŸŒ¾ Crop Recommendation</h2>
                <form onSubmit={handleSubmit}>
                    {['Nitrogen(N)', 'Phosphorus(P)', 'Potassium(K)', 'Temperature(Â°C)', 'Humidity(%)', 'pH of soil', 'Rainfall(mm)'].map(field => (
                        <div key={field} className="form-group">
                            <label>{field.toUpperCase()}</label>
                            <input
                                type="number"
                                name={field}
                                step="any"
                                value={formData[field]}
                                onChange={handleChange}
                                placeholder={placeholders[field]}
                                required
                            />
                        </div>
                    ))}
                    <button type="submit">Get Recommendation</button>
                </form>
                {result && <div className="result-box">Recommended Crop: <strong>{result}</strong></div>}
            </div>
        </div>
    );
}

export default CropForm;
