import React, { useEffect, useState } from "react";
import "./CardSlider.css";

const CardSlider = () => {
  const [visible, setVisible] = useState(false);

  useEffect(() => {
    setTimeout(() => {
      setVisible(true);
    }, 300); 
  }, []);

  const cardData = [
    { id: 1, image: "/image1.jpg", title: "Card 1", desc: "This is card one." },
    { id: 2, image: "/image2.jpg", title: "Card 2", desc: "This is card two." },
    { id: 3, image: "/image3.jpg", title: "Card 3", desc: "This is card three." },
    { id: 4, image: "/image4.jpg", title: "Card 4", desc: "This is card four." },
  ];

  return (
    <div className="card-container">
      {cardData.map((card, index) => (
        <div key={card.id} className={`card ${visible ? "slide-up" : ""}`} style={{ animationDelay: `${index * 0.2}s` }}>
          <img src={card.image} alt={card.title} className="card-img" />
          <h3>{card.title}</h3>
          <p>{card.desc}</p>
        </div>
      ))}
    </div>
  );
};

export default CardSlider;
