import React, { useState } from 'react';
import './ImageUpload.css';

const ImageUpload = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [prediction, setPrediction] = useState('');
  const [imagePreview, setImagePreview] = useState(null);
  const [lineMoving, setLineMoving] = useState(false); // To track line animation state

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);

    // Generate a preview of the selected image
    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreview(reader.result);
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert("Please select a file first!");
      return;
    }

    const formData = new FormData();
    formData.append('image', selectedFile);

    // Start the upload and line animation
    setPrediction(''); // Reset prediction before fetching
    setLineMoving(true); // Start the line animation

    // Delay the display of prediction
    setTimeout(async () => {
      try {
        const response = await fetch('http://localhost:8000/predict/', {
          method: 'POST',
          body: formData,
        });

        const data = await response.json();
        setPrediction(data.prediction); // Assuming Django returns { prediction: 'some result' }
      } catch (error) {
        console.error("Error uploading file:", error);
      }

      setLineMoving(false); // Stop the line animation once prediction is ready
    }, 2000); // 2-second delay for better user experience
  };

  return (
    <div className="image-upload-container">
      <h2 className="upload-heading">Upload an Image for Prediction</h2>
      
      <div className="file-input-wrapper">
        <input
          type="file"
          accept="image/*"
          onChange={handleFileChange}
          className="file-input"
        />
        <button
          className="upload-btn"
          onClick={handleUpload}
        >
          Upload
        </button>
      </div>

      {/* Show image preview if there's one */}
      {imagePreview && (
        <div className="image-preview-wrapper">
          <img src={imagePreview} alt="Uploaded Preview" className="image-preview" />
          
          {/* Vertical line animation */}
          {lineMoving && <div className="moving-line"></div>}
        </div>
      )}

      {/* After 2-second delay, show prediction */}
      {prediction && <p className="prediction-text"><strong>Prediction:</strong> {prediction}</p>}
    </div>
  );
};

export default ImageUpload;
