import { useEffect, useState } from 'react';
import axios from 'axios';

const CommunityFeed = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    fetchPosts();
  }, []);

  const fetchPosts = async () => {
    const res = await axios.get('http://localhost:8000/api/community-posts/');
    setPosts(res.data);
  };

  const likePost = async (id) => {
    await axios.post(`http://localhost:8000/api/community-posts/${id}/like/`);
    fetchPosts();
  };

  const addComment = async (id, content) => {
    await axios.post(`http://localhost:8000/api/${id}/comment/`, { content });
    fetchPosts();
  };

  return (
    <div>
      <h2>Community Feed</h2>
      {posts.map(post => (
        <div key={post.id} style={{ border: '1px solid #ccc', padding: 10, marginBottom: 10 }}>
          <p><strong>{post.username}</strong>: {post.content}</p>
          <p>{post.likes} Likes</p>
          <button onClick={() => likePost(post.id)}>Like</button>

          <div>
            <h4>Comments</h4>
            {post.comments.map(comment => (
              <p key={comment.id}><strong>{comment.username}</strong>: {comment.content}</p>
            ))}
            <CommentBox postId={post.id} onAddComment={addComment} />
          </div>
        </div>
      ))}
    </div>
  );
};

const CommentBox = ({ postId, onAddComment }) => {
  const [text, setText] = useState('');

  const submit = () => {
    if (text) {
      onAddComment(postId, text);
      setText('');
    }
  };

  return (
    <div>
      <input value={text} onChange={e => setText(e.target.value)} placeholder="Write a comment" />
      <button onClick={submit}>Comment</button>
    </div>
  );
};

export default CommunityFeed;
