import React, { useEffect, useState, useRef } from "react";
import "./CardSlider.css";

const CardSlider = () => {
  const [visible, setVisible] = useState(false);
  const cardRef = useRef(null);

  useEffect(() => {
    const currentRef = cardRef.current; // Store the reference before using it
  
    if (currentRef) {
      const observer = new IntersectionObserver(
        (entries) => {
          if (entries[0].isIntersecting) {
            setVisible(true);
          }
        },
        { threshold: 0.3 }
      );
  
      observer.observe(currentRef);
  
      return () => {
        observer.unobserve(currentRef);
      };
    }
  }, []);
  

  const cardData = [
    { id: 1, image: "/images/image1.jpg", title: "Card 1", desc: "This is card one." },
    { id: 2, image: "/images/image2.jpg", title: "Card 2", desc: "This is card two." },
    { id: 3, image: "/images/image3.jpg", title: "Card 3", desc: "This is card three." },
    { id: 4, image: "/images/image4.jpg", title: "Card 4", desc: "This is card four." },
  ];

  return (
    <div className="card-container" ref={cardRef}>
      {cardData.map((card, index) => (
        <div key={card.id} className={`card ${visible ? "slide-up" : ""}`} data-index={index}>
          <img src={card.image} alt={card.title} className="card-img" />
          <h3>{card.title}</h3>
          <p>{card.desc}</p>
        </div>
      ))}
    </div>
  );
};

export default CardSlider;
