import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Community() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:8000/community/')
      .then(res => setPosts(res.data));
  }, []);

  const toggleLike = async (id) => {
    const token = localStorage.getItem('access');
    await axios.post(`http://localhost:8000/posts/${id}/like/`, {}, {
      headers: { Authorization: `Bearer ${token}` }
    });
    const updated = await axios.get('/api/community/');
    setPosts(updated.data);
  };

  const addComment = async (id, text) => {
    const token = localStorage.getItem('access');
    await axios.post(`http://localhost:8000/posts/${id}/comment/`, { text }, {
      headers: { Authorization: `Bearer ${token}` }
    });
    const updated = await axios.get('http://localhost:8000/community/');
    setPosts(updated.data);
  };

  return (
    <div>
      {posts.map(post => (
        <div key={post.id} style={{ border: '1px solid #ccc', marginBottom: 20 }}>
          <h4>@{post.user}</h4>
          <img src={post.image} width="300" alt="" />
          <p>{post.content}</p>
          <button onClick={() => toggleLike(post.id)}>Like</button> ❤️ {post.total_likes}
          <div>
            <input placeholder="Comment" onKeyDown={e => {
              if (e.key === 'Enter') addComment(post.id, e.target.value);
            }} />
          </div>
          {post.comments.map(comment => (
            <p key={comment.id}><strong>{comment.username}</strong>: {comment.text}</p>
          ))}
        </div>
      ))}
    </div>
  );
}

export default Community;
