import React, { useState, useEffect } from "react";
import axios from "axios";

const Weather = () => {
  const [weather, setWeather] = useState(null);
  const API_KEY = "7ebb10fee517057688f6b7c9e1ba938c";
  const CITY = "New Delhi"; // üîπ Change this to your preferred city

  useEffect(() => {
    const fetchWeather = async () => {
      try {
        const response = await axios.get(
          `https://api.openweathermap.org/data/2.5/weather?q=${CITY}&units=metric&appid=${API_KEY}`
        );
        setWeather(response.data);
      } catch (error) {
        console.error("Error fetching weather data:", error);
      }
    };

    fetchWeather();
  }, []);

  return (
    <div className="weather-container">
      {weather ? (
        <div>
          <h2>Weather in {weather.name}</h2>
          <p>üå° Temperature: {weather.main.temp}¬∞C</p>
          <p>üå¨ Wind Speed: {weather.wind.speed} m/s</p>
          <p>‚òÅ Condition: {weather.weather[0].description}</p>
          <img
            src={`https://openweathermap.org/img/wn/${weather.weather[0].icon}@2x.png`}
            alt="Weather Icon"
          />
        </div>
      ) : (
        <p>Loading weather data...</p>
      )}
    </div>
  );
};

export default Weather;
