import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { useEffect, useState } from 'react';
import Navbar from './components/navbar';
import HomePage from './components/home_page';
import Weather from './components/Weather';
import TaskList from './components/TaskList';
import ImageUpload from './components/ImageUpload';
import Register from './components/Register';
import Login from './components/Login';
import CommunityPage from './components/CommunityPage';

function App() {

  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [loading, setLoading] = useState(true); // ✅ Added loading state

  useEffect(() => {
    const token = localStorage.getItem('access');
    setIsAuthenticated(!!token);
    setLoading(false); // ✅ Auth check complete

  }, []);

  const PrivateRoute = ({ element }) => {
    return isAuthenticated ? element : <Navigate to="/login" />;
  };

  if (loading) return null; // ✅ Wait until auth check is done

  return (
    <Router>
      {isAuthenticated && <Navbar onLogout={() => setIsAuthenticated(false)} />}

      <Routes>
        <Route path="/login" element={<Login onLogin={() => setIsAuthenticated(true)} />} />
        <Route path="/register" element={<Register />} />

        <Route path="/" element={<PrivateRoute element={<HomePage />} />} />
        <Route path="/weather" element={<PrivateRoute element={<Weather />} />} />
        <Route path="/tasklist" element={<PrivateRoute element={<TaskList />} />} />
        <Route path="/predict" element={<PrivateRoute element={<ImageUpload />} />} />
        <Route path="/community" element={<PrivateRoute element={<CommunityPage />} />} />
      </Routes>
    </Router>
  );
}

export default App;
