// CommunityPage.js
import React, { useEffect, useState } from "react";
import axios from "axios";

const CommunityPage = () => {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8000/api/posts/") // adjust to your backend URL
      .then((res) => setPosts(res.data))
      .catch((err) => console.log(err));
  }, []);

  return (
    <div>
      {posts.map((post) => (
        <div key={post.id} className="border p-4 m-2 rounded shadow">
          <h4>{post.farmer.name}</h4>
          <p>{post.content}</p>
          {post.image && <img src={post.image} alt="post" className="w-full h-auto" />}
          <p>{post.likes_count} likes</p>
          <div>
            {post.comments.map((comment) => (
              <div key={comment.id}>
                <strong>{comment.farmer.name}</strong>: {comment.comment}
              </div>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
};

export default CommunityPage;
