import React, {useState} from 'react';
import './navbar.css';  
import { NavLink, useNavigate } from 'react-router-dom';

const Navbar = ({ onLogout, overdueCount }) => {
  const navigate = useNavigate();
  const username = sessionStorage.getItem('username');
  const [isMenuOpen, setIsMenuOpen] = useState(false); // State to toggle menu visibility

  const handleLogout = () => {
    sessionStorage.removeItem('username');
    sessionStorage.removeItem('access_token');
    onLogout(); 
    navigate('/login');
  };
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <nav className="navbar">
      <h2 className="logo"><span className='logo-one'>Fresh</span>Finds</h2>

      {/* Toggle Button (Hamburger Icon) */}
      <div className="hamburger" onClick={toggleMenu}>
        <div className="line"></div>
        <div className="line"></div>
        <div className="line"></div>
      </div>

      {/* Navigation Links */}
      <ul className={`nav-links center-links ${isMenuOpen ? "open" : ""}`}>
        <li>
          <NavLink to="/" className={({ isActive }) => isActive ? "active" : ""}>Home</NavLink>
        </li>
        <li>
          <NavLink to="/weather" className={({ isActive }) => isActive ? "active" : ""}>Weather</NavLink>
        </li>
        <li>
          <NavLink to="/tasklist" className={({ isActive }) => isActive ? "active" : ""}>
            TaskList
            {overdueCount > 0 && (
              <span className="notification-badge">{overdueCount}</span>
            )}
          </NavLink>
        </li>
        <li>
          <NavLink to="/predict" className={({ isActive }) => isActive ? "active" : ""}>Disease Prediction</NavLink>
        </li>
        <li>
          <NavLink to="/recommend-crop" className={({ isActive }) => isActive ? "active" : ""}>Crop Recommendation</NavLink>
        </li>
        <li>
          <NavLink to="/crop-feature" className={({ isActive }) => isActive ? "active" : ""}>Crop Features</NavLink>
        </li>
        <li>
          <NavLink to="/vendors" className={({ isActive }) => isActive ? "active" : ""}>Vendors</NavLink>
        </li>
        <li><a href="/contact">Contact</a></li>
      </ul>

      <ul className="nav-links right-links">
        <li><button onClick={handleLogout} className="logout-btn">Logout</button></li>
        {username && <li className="username-display">Hi, {username}</li>}
      </ul>
    </nav>
  );
};

export default Navbar;
