import React, { useEffect, useState } from 'react';
import axios from 'axios';

const VendorDashboard = () => {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem('access_token'); // ⬅️ get the token

    axios.get('http://localhost:8000/vendor-requests/', {
      headers: {
        Authorization: `Bearer ${token}`, // ⬅️ attach the token here
      },
    })
    .then(res => setRequests(res.data))
    .catch(err => {
      console.error('Failed to fetch vendor requests:', err);
      if (err.response && err.response.status === 401) {
        alert('Unauthorized. Please login again.');
      }
    });
  }, []);

  return (
    <div>
      <h2>Sell Requests</h2>
      <ul>
        {requests.map(req => (
          <li key={req.id}>
            {req.crop} - {req.quantity}kg from {req.farmer_name} ({req.location}) 📞 {req.contact_number}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default VendorDashboard;
