import React, { useState, useEffect, useRef } from "react";
import "./Cards.css"; // Import external CSS

const Cards = () => {
  // Initialize refs for each card
  const cardRefs = [useRef(null), useRef(null), useRef(null), useRef(null)];

  const [visible, setVisible] = useState([false, false, false, false]);

  useEffect(() => {
    const observers = cardRefs.map((cardRef, index) => {
      if (!cardRef.current) return null;

      const observer = new IntersectionObserver(
        (entries) => {
          entries.forEach((entry) => {
            setVisible((prev) => {
              const newState = [...prev];
              newState[index] = entry.isIntersecting;
              return newState;
            });
          });
        },
        { threshold: 0.3 }
      );

      observer.observe(cardRef.current);
      return observer;
    });

    return () => {
      observers.forEach((observer, index) => {
        if (observer && cardRefs[index].current) {
          observer.unobserve(cardRefs[index].current);
        }
      });
    };
  }, []); // ✅ Empty dependency array to avoid unnecessary re-renders

  return (
    <div className="cards-container">
      {["Image 1", "Image 2", "Image 3", "Image 4"].map((text, index) => (
        <div
          key={index}
          ref={cardRefs[index]}
          className={`card ${visible[index] ? "visible" : ""}`}
        >
          <img src={`/images/image${index + 1}.jpg`} alt={`Card ${index + 1}`} />
          <p>{text} - Some info about this card.</p>
        </div>
      ))}
    </div>
  );
};

export default Cards;
