import { useState } from 'react';
import axios from 'axios';

const CommunityPostCreate = () => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');

  const submitPost = async () => {
    try {
      await axios.post(
        'http://localhost:8000/api/community-posts/',
        {
          title,
          content
        },
        {
          headers: {
            // If you're using JWT tokens for auth, add Authorization header
            Authorization: `Bearer ${localStorage.getItem('token')}`,  // adjust if your token is stored differently
            'Content-Type': 'application/json'
          }
        }
      );
      setTitle('');
      setContent('');
      alert('Post created!');
    } catch (error) {
      console.error('Error creating post:', error);
      alert('Failed to create post.');
    }
  };

  return (
    <div>
      <h2>Create Community Post</h2>
      <input
        type="text"
        placeholder="Enter title"
        value={title}
        onChange={e => setTitle(e.target.value)}
      />
      <br />
      <textarea
        placeholder="Enter content"
        value={content}
        onChange={e => setContent(e.target.value)}
      />
      <br />
      <button onClick={submitPost}>Post</button>
    </div>
  );
};

export default CommunityPostCreate;
