import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './SellCropForm.css';

const SellCropForm = () => {
  const [form, setForm] = useState({
    crop: '',
    quantity: '',
    location: '',
    contact_number: '',
    vendor: '',  // We'll set vendor manually or by default
  });

  const [farmerId, setFarmerId] = useState(null);

  useEffect(() => {
    // Retrieve farmer (user) id from localStorage
    const storedFarmerId = localStorage.getItem('user_id'); 
    if (storedFarmerId) {
      setFarmerId(storedFarmerId);
    } else {
      alert('You are not logged in.');
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('access_token');

    if (!token || !farmerId) {
      alert('You are not logged in.');
      return;
    }

    try {
      const { crop, quantity, location, contact_number, vendor } = form;

      const response = await axios.post(
        'http://localhost:8000/sell-request/',
        {
          crop,
          quantity,
          location,
          contact_number,
          vendor: vendor || 1, // set default vendor id = 1 if not selected
          farmer: farmerId,     // use stored farmer id
        },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );

      console.log('Sell request successful:', response.data);
      alert('Request sent successfully!');
    } catch (error) {
      console.error('Error sending request:', error.response?.data || error.message);
      alert('Failed to send sell request');
    }
  };

  return (
    <div className="form-container">
      <h2 className="form-heading">Sell Crop Request</h2>
      <form onSubmit={handleSubmit} className="sell-crop-form">
        <input
          type="text"
          placeholder="Crop"
          className="form-input"
          value={form.crop}
          onChange={(e) => setForm({ ...form, crop: e.target.value })}
          required
        />
        <input
          type="number"
          placeholder="Quantity"
          className="form-input"
          value={form.quantity}
          onChange={(e) => setForm({ ...form, quantity: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Location"
          className="form-input"
          value={form.location}
          onChange={(e) => setForm({ ...form, location: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Contact Number"
          className="form-input"
          value={form.contact_number}
          onChange={(e) => setForm({ ...form, contact_number: e.target.value })}
          required
        />

        {/* Optional: Vendor ID input */}
        <input
          type="number"
          placeholder="Vendor ID (optional)"
          className="form-input"
          value={form.vendor}
          onChange={(e) => setForm({ ...form, vendor: e.target.value })}
        />

        <button type="submit" className="submit-button">Send Request</button>
      </form>
    </div>
  );
};

export default SellCropForm;
