import React, { useState, useEffect } from "react";
import axios from "axios";
import "./TaskList.css";

const API_URL = "http://localhost:8000";

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState({ title: "", description: "", date: "", time: "" });

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get(API_URL);
      setTasks(response.data);
    } catch (error) {
      console.error("Error fetching tasks:", error);
    }
  };

  const addTask = async () => {
    if (!newTask.title || !newTask.date || !newTask.time) {
      alert("Please fill in all required fields!");
      return;
    }
    try {
      await axios.post(API_URL, { ...newTask, completed: false },
        { headers: { "Content-Type": "application/json" } }
      );
      fetchTasks();
      setNewTask({ title: "", description: "", date: "", time: "" });
    } catch (error) {
      console.error("Error adding task:", error);
    }
  };

  const toggleComplete = async (id, completed) => {
    try {
      await axios.patch(`${API_URL}${id}/`, { completed: !completed },
        { headers: { "Content-Type": "application/json" } } 
      );
      fetchTasks();
    } catch (error) {
      console.error("Error updating task:", error);
    }
  };

  const deleteTask = async (id) => {
    try {
      await axios.delete(`${API_URL}${id}/`);
      fetchTasks();
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  return (
    <div className="task-container">
      <h2>Farmer's Task List</h2>

      <div className="task-input">
        <input
          type="text"
          placeholder="Task Title"
          value={newTask.title}
          onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Description"
          value={newTask.description}
          onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
        />
        <input
          type="date"
          value={newTask.date}
          onChange={(e) => setNewTask({ ...newTask, date: e.target.value })}
        />
        <input
          type="time"
          value={newTask.time}
          onChange={(e) => setNewTask({ ...newTask, time: e.target.value })}
        />
        <button onClick={addTask}>Add Task</button>
      </div>

      <ul className="task-list">
    {tasks.map((task, index) => (
    <li key={task.id || index} className={task.completed ? "completed" : ""}>
      <div>
        <h3>{task.title}</h3>
        <p>{task.description || "No description provided."}</p>
        <p>Date: {task.date}</p>
        <p>Time: {task.time}</p>
      </div>
      <div className="task-buttons">
        <button onClick={() => toggleComplete(task.id, task.completed)}>
          {task.completed ? "Undo" : "Complete"}
        </button>
        <button onClick={() => deleteTask(task.id)}>Delete</button>
      </div>
    </li>
  ))}
</ul>

    </div>
  );
};

export default TaskList;
