import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const SellCropForm = () => {
  const { vendorId } = useParams();
  const [form, setForm] = useState({ crop: '', quantity: '', location: '', contact_number: '' });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await axios.post('http://localhost:8000/sell-request/', { ...form, vendor: vendorId });
    alert("Request sent!");
  };

  return (
    <form onSubmit={handleSubmit}>
      <input placeholder="Crop" onChange={(e) => setForm({...form, crop: e.target.value})} />
      <input placeholder="Quantity" type="number" onChange={(e) => setForm({...form, quantity: e.target.value})} />
      <input placeholder="Location" onChange={(e) => setForm({...form, location: e.target.value})} />
      <input placeholder="Contact" onChange={(e) => setForm({...form, contact_number: e.target.value})} />
      <button type="submit">Send</button>
    </form>
  );
};

export default SellCropForm;
