import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './SellCropForm.css'; 
import { useParams } from 'react-router-dom';

const SellCropForm = () => {
  const { vendorId } = useParams();
  const [vendors, setVendors] = useState([]);
  const [selectedVendorId, setSelectedVendorId] = useState(vendorId || '');
  const [form, setForm] = useState({ crop: '', quantity: '', location: '', contact_number: '' });

  useEffect(() => {
    // Fetch vendors dynamically
    const fetchVendors = async () => {
      try {
        const token = localStorage.getItem('access_token');
        const response = await axios.get('http://localhost:8000/vendors/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setVendors(response.data);
      } catch (error) {
        console.error('Error fetching vendors:', error);
      }
    };

    fetchVendors();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('access_token');
    if (!token) {
      alert("You are not logged in.");
      return;
    }

    try {
      const { crop, quantity, location, contact_number } = form;
      const response = await axios.post(
        'http://localhost:8000/sell-request/',
        {
          crop,
          quantity,
          location,
          contact_number,
          vendor: selectedVendorId // Now dynamically selected vendor
        },
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
          },
        }
      );
  
      console.log('Sell request successful:', response.data);
      alert('Request sent successfully!');
    } catch (error) {
      console.error('Error sending request:', error.response?.data, error.response?.status, error.response?.statusText);
      alert('Failed to send sell request');
    }
  };

  return (
    <div className="form-container">
      <h2 className="form-heading">Sell Crop Request</h2>
      <form onSubmit={handleSubmit} className="sell-crop-form">

        {/* Dropdown to select vendor */}
        <select 
          className="form-input"
          value={selectedVendorId}
          onChange={(e) => setSelectedVendorId(e.target.value)}
          required
        >
          <option value="">Select Vendor</option>
          {vendors.map((vendor) => (
            <option key={vendor.id} value={vendor.id}>
              {vendor.company_name} ({vendor.location})
            </option>
          ))}
        </select>

        <input 
          type="text" 
          placeholder="Crop" 
          className="form-input" 
          onChange={(e) => setForm({...form, crop: e.target.value})} 
          required
        />
        <input 
          type="number" 
          placeholder="Quantity" 
          className="form-input" 
          onChange={(e) => setForm({...form, quantity: e.target.value})} 
          required
        />
        <input 
          type="text" 
          placeholder="Location" 
          className="form-input" 
          onChange={(e) => setForm({...form, location: e.target.value})} 
          required
        />
        <input 
          type="text" 
          placeholder="Contact Number" 
          className="form-input" 
          onChange={(e) => setForm({...form, contact_number: e.target.value})} 
          required
        />
        <button type="submit" className="submit-button">Send Request</button>
      </form>
    </div>
  );
};

export default SellCropForm;
