import React from 'react';
import './navbar.css';  
import { NavLink, useNavigate } from 'react-router-dom';

const Navbar = ({ onLogout }) => {
  const navigate = useNavigate();
  const username = localStorage.getItem('username');

  const handleLogout = () => {
    localStorage.removeItem('access_token');
    localStorage.removeItem('username');
    onLogout(); 
    navigate('/login');
  };

  return (
    <nav className="navbar">
      <h2 className="logo"><span className='logo-one'>Fresh</span>Finds</h2>
      <ul className="nav-links">
        <li><NavLink to="/" className={({ isActive }) => isActive ? "active" : ""}>Home</NavLink></li>
        <li><NavLink to="/weather" className={({ isActive }) => isActive ? "active" : ""}>Weather</NavLink></li>
        <li><NavLink to="/tasklist" className={({ isActive }) => isActive ? "active" : ""}>TaskList</NavLink></li>
        <li><NavLink to="/predict" className={({ isActive }) => isActive ? "active" : ""}>Disease Prediction</NavLink></li>
        {/* <li><NavLink to="/community" className={({ isActive }) => isActive ? "active" : ""}>Community</NavLink></li>
        <li><NavLink to="/posts" className={({ isActive }) => isActive ? "active" : ""}>Post</NavLink></li> */}
        <li><NavLink to="/recommend-crop" className={({ isActive }) => isActive ? "active" : ""}>Crop Recommendation</NavLink></li>
        <li><a href="/contact">Contact</a></li>
        <li><button onClick={handleLogout} className="logout-btn">Logout</button></li>
        {username && <li className="username-display">Hi, {username}</li>}      
      </ul>
    </nav>
  );
};

export default Navbar;
