import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './VendorDashboard.css'; // Make sure you import your css

const VendorDashboard = () => {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    const fetchRequests = async () => {
      try {
        const token = localStorage.getItem('access_token');
        const response = await axios.get('http://localhost:8000/vendor-requests/', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setRequests(response.data);
      } catch (err) {
        console.error('Error fetching vendor requests:', err);
      }
    };

    fetchRequests();
  }, []);

  return (
    <div className="background-wrapper">
      <div className="dashboard-container">
        <h2 className="dashboard-heading">Sell Requests for You</h2>
        <ul className="request-list">
          {requests.map((req) => (
            <li className="request-item" key={req.id}>
              <strong>{req.crop}</strong> - {req.quantity}kg from {req.location} ({req.contact_number})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default VendorDashboard;
